<?phpclass Compandsave_Catalog_Model_Convert_Adapter_Product    extends Mage_Catalog_Model_Convert_Adapter_Product{	protected $mapping_data = '';	protected $sku_data = '';		public function saveRow(array $importData)    {		parent::saveRow($importData);		if(!empty($importData['mapping_sku']))			$this->mapping_data = $importData['mapping_sku'];		$this->sku_data = $importData['sku'];		$product_in = Mage::getModel('catalog/product')->loadByAttribute('sku',$this->sku_data);		if($product_in->getTypeId() === 'simple' ){// and getMappingSku() != ''                    			//Mage::register('product', $product);			$childId = $product_in->getId(); //get simple product ID			$products_links = Mage::getModel('catalog/product_link_api');			$coreResource = Mage::getSingleton('core/resource');			$conn = $coreResource->getConnection('core_read');			$select = $conn->select()				->from($coreResource->getTableName('catalog/product_relation'), array('parent_id'))				->where('child_id = ?', $childId);			$result = $conn->fetchCol($select);			foreach($result as $item_list){				$products_links->remove ("grouped",$item_list,$childId); //remove all link due to save or update			}						$prd_id = explode(',',$this->mapping_data);			foreach($prd_id as $item){				$get_product = Mage::getModel('catalog/product')->loadByAttribute('sku',$item);				if(!empty($get_product)){					$parentId = $get_product->getId(); //get group product ID					$products_links->assign ("grouped",$parentId,$childId); //insert new link only				}			}						                    		}                    		      }}