<?phpclass Compandsave_Catalog_Model_Convert_Adapter_Product    extends Mage_Catalog_Model_Convert_Adapter_Product{	protected $mapping_data = null;	protected $sku_data = null;		public function saveRow(array $importData)    {		parent::saveRow($importData);		if(!empty($importData['mapping_sku']))			$this->mapping_data = $importData['mapping_sku'];		$this->sku_data = $importData['sku'];		$product_in = Mage::getModel('catalog/product')->loadByAttribute('sku',$this->sku_data);		if( $product_in->getTypeId() === 'simple'  and strlen($this->mapping_data)){// and getMappingSku() != ''            $product_in->setCompatibleCatid($this->mapping_data);            $product_in->setIsMassupdate(true);            $product_in->setExcludeUrlRewrite(true);            $product_in->save();                    		}	      }}